mapper.select:
SELECT * FROM (
    SELECT
        row_number() over (ORDER BY system_create_date DESC )AS no,

        uuid,
        temp_yn,
        system_create_date, 
        system_create_userid, 
        system_update_date, 
        system_update_userid 
    FROM COMMON_FILE


        AND uuid = #{uuid}
        AND temp_yn = #{temp_yn}
        AND system_create_date = #{system_create_date}
        AND system_create_userid = #{system_create_userid}
        AND system_update_date = #{system_update_date}
        AND system_update_userid = #{system_update_userid}

        AND uuid LIKE CONCAT('%', #{_uuid}, '%')
        AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
        AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
        AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
        AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

) AS sub

LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM COMMON_FILE


    AND uuid = #{uuid}
    AND temp_yn = #{temp_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}

    AND uuid LIKE CONCAT('%', #{_uuid}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

SELECT

    uuid,
    temp_yn,
    system_create_date,
    system_create_userid,
    system_update_date,
    system_update_userid
FROM COMMON_FILE


    AND uuid = #{uuid}
    AND temp_yn = #{temp_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}

    AND uuid LIKE CONCAT('%', #{_uuid}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

mapper.insert:
INSERT INTO COMMON_FILE(

     uuid
    , temp_yn
    , system_create_date 
    , system_create_userid 

    , system_update_date
    , system_update_userid
)
VALUES(

     #{uuid}
    , #{temp_yn}
    , #{system_create_date}
    , #{system_create_userid}

    , #{system_update_date}
    , #{system_update_userid}
)

mapper.delete:
DELETE FROM COMMON_FILE


    AND uuid = #{uuid}

    AND temp_yn = #{temp_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}
